import React, { useState } from "react";
import Button from "../Button";

function Header() {
  const [Manu, setManu] = useState(false)
  const [Search, setSearch] = useState(true)
  const [className, setclassName] = useState(
    {
      manu : {
        button : 'fas fa-bars',
        display : 'display-none'
      },
      search : {
          button : 'fas fa-search',
          display : 'display-none'
      }

    }
  )
  const clickedManu = () => {
      setManu(prevState => !prevState)
      console.log(Manu);
      if(Manu){
        setclassName({...className, manu : {
          button : 'fas fa-times',
          display: 'flex'
        }  })
      }
      else{
        setclassName({...className, manu : {
          button : 'fas fa-bars',
          display: 'display-none'
        } })

      }
  } 
  const clickedSearch = () => {
    setSearch(prevState => !prevState)
    console.log(Search);
    if(Search){
      setclassName({...className, search : {
        button : 'fas fa-times',
        display: 'flex'
      }  })
    }
    else{
      setclassName({...className, manu : {
        button : 'fas fa-bars',
        display: 'display-none'
      } })

    }
  }

  return (
    <div id="header-block">
      <div className="container">
        <span id="header-logo">
          <h1>Switch</h1>
        </span>
        <div id="header-flexbox" className="flex">
          <nav id="header-manu">
          <i class={className.manu.button} onClick = {clickedManu}></i>
            <ul className = {className.manu.display}>
              <li>
                <div></div>
                <a href = '#home-container'>home</a>
              </li>
              <li>
                <div></div>
                <a href = '#what-we-do'>what we do?</a>
              </li>
              <li>
                <div></div>
                <a href = '#testimonials'>testimonial</a>
              </li>
              <li>
                <div></div>
                <a href = '#footer-container'>contact us</a>
              </li>
            </ul>
          </nav>
          <div id = 'header-input-wrapper'>
            <div id = 'search-button' className = 'flex'>
            <i class="fas fa-search" onClick = {clickedSearch}></i>
            </div>
          <div id="header-input">
            <input id = 'input' type="text" placeholder="Search" />
            <button>
              <i class="fas fa-search"></i>
            </button>
          </div>
          </div>
        </div>
      </div>
      <div id="home-container">
        <div id="home-content-center">
          <div id="home-content-flex" className="flex">
            <h1>Art is Eternal Happiness</h1>
            <Button name="work with us" />
          </div>
        </div>
        <div id = 'home-down-button'>
          <button className = 'btn'>Scroll Down</button>
        </div>
      </div>
    </div>
  );
}

export default Header;
